type item = {
  id: int,
  title: string,
  completed: bool,
};

let se = ReasonReact.stringToElement;

let module TodoItem = {
  let component = ReasonReact.statelessComponent "TodoItem";
  let make ::item ::onToggle children => {
    ...component,
    render: fun _ =>
      <div className="item" onClick=(fun evt => onToggle())>
        <input
          _type="checkbox"
          checked=(Js.Boolean.to_js_boolean item.completed)
        />
        (se item.title)
      </div>
  };
};

let valueFromEvent evt: string =>
  (evt
    |> ReactEventRe.Form.target
    |> ReactDOMRe.domElementToObj
  )##value;

let module Input = {
  type state = string;
  let component = ReasonReact.statefulComponent "Input";
  let make ::onSubmit _ => {
    ...component,
    initialState: fun () => "",
    render: fun {state: text, update} =>
      <input
        value=text
        _type="text"
        placeholder="Write something to do"
        onChange=(update (fun evt _ =>
          ReasonReact.Update (valueFromEvent evt)
        ))
        onKeyDown=(update (fun evt {state: text} =>
          if (ReactEventRe.Keyboard.key evt == "Enter") {
            onSubmit text;
            ReasonReact.Update "";
          } else {
            ReasonReact.NoUpdate
          }
        ))
      />
  };
};

type state = {
  items: list item,
};

let component = ReasonReact.statefulComponent "TodoApp";

let lastId = ref 0;
let newItem text => {
  lastId := !lastId + 1;
  {id: !lastId, title: text, completed: false};
};

let toggleItem items id => {
  List.map
  (fun item => item.id === id ? {...item, completed: not item.completed} : item)
  items;
};

let make children => {
  ...component,
  initialState: fun () => {
    items: [{
      id: 0,
      title: "Write some things to do",
      completed: false,
    }]
  },
  render: fun {state: {items}, update} => {
    let numItems = List.length items;

    <div className="app">
      <div className="title">
        (se "What to do")
        <Input
          onSubmit=(update (fun text {state} => {
            ReasonReact.Update {
              ...state,
              items: [newItem text, ...state.items]
            }
          }))
        />
      </div>
      <div className="items">
        (ReasonReact.arrayToElement
          (Array.of_list
            (List.map (fun item => <TodoItem
              key=(string_of_int item.id)
              onToggle=(update (fun () {state} =>
                ReasonReact.Update {
                  ...state,
                  items: toggleItem items item.id
                }
              ))
              item
            />) items)
          )
        )
      </div>
      <div className="footer">
        (se ((string_of_int numItems) ^ " items"))
      </div>
    </div>
  }
};

embed.base.css:
/* reset */
body, div {
    display: flex;
    flex-direction: column;
    box-sizing: border-box;
    flex-shrink: 0;
}

body {
  font: 14px "Century Gothic", Futura, sans-serif;
}

#root {
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
}

.app {
  width: 280px;
  max-height: 600px;
  flex: 1;
  box-shadow: 0 2px 15px #ccc;
  margin: 10px;
  align-items: stretch;
}

.title {
  font-size: 20px;
  align-items: center;
  padding: 20px;
}

.items {
  margin: 10px 0;
  text-align: center;
  flex: 1;
  overflow: auto;
}

.footer {
  align-items: flex-start;
  padding: 5px 10px;
}

.item {
  display: flex;
  padding: 10px 20px;
  align-items: center;
  flex-direction: row;
}

.checkbox {
  margin-right: 20px;
}

input[type="checkbox"] {
  margin-right: 10px;
}

input[type="text"] {
  align-self: stretch;
  padding: 3px 5px;
  margin-top: 20px;
}
